mapper.select:
select * from
    (
        select rownum as rnum, a.* from
            (
                select
                        follow_member_no,(select member_nickname from member where member_no=f.follow_member_no) as memberNickname,(select member_grade from member where member_no=f.follow_member_no) as memberGrade
                from follow f where member_no=#{memberNo}
            ) a
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}
