mapper.select:
select * from
    (
        select rownum as rnum, t.* from
            (
                select
                   town_no,town_gu,town_dong from town
            ) t
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}

select * from
    (
        select rownum as rnum, t.* from
            (
                select
                    town_no,town_gu,town_dong from town where town_gu = #{gu}
            ) t
        where rownum<=#{endRownum}
    )
where rnum>=#{startRownum}
